mapper.select:
select chat_room_id
from chat_room_member_link
where student_id = #{studentId}

select student_id
from chat_room_member_link
where chat_room_id = #{chatRoomId}

select teacher_id
from chat_room
where chat_room_id = #{chatRoomId}

select chat_room_id
from chat_room
where teacher_id = #{teacherId}

select link_id
from chat_room_member_link
where student_id = #{arg0}
  and chat_room_id = #{arg1}

select chat_room_name
from chat_room
where chat_room_id = #{arg0}

select count(*)
from chat_room_member_link
where student_id = #{arg0}
  and chat_room_id = #{arg1}
